import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Client } from './client';
//import { Client } from './Client';
//import { Client } from './Client';
import { clientService } from './client.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'clientapp';

    public clients: Client[];
    public editClient!: Client;
    public deleteClient!: Client; 

  constructor(private clientservice: clientService){
    this.clients=[];






  }
  ngOnInit(): void {
    this.getClients();

  }



  public getClients(): void{

    this.clientservice.getClient().subscribe({
 next:(response: Client[]) => this.clients=response,
 error: (error:HttpErrorResponse) => error.message,

 complete: () => console.info('complete') 
    })

    }

public onAddClient(addForm: NgForm): void {
  document.getElementById('add-employee-form')?.click();
  this.clientservice.addClient(addForm.value).subscribe({
  next:(response: Client) => {console.log(response); this.getClients(); addForm.reset();},
  error: (error:HttpErrorResponse) => error.message,
  complete: () => console.info('complete') 


  })
}




public onUpdateClient(id: number ): void {

  this.clientservice.update(id).subscribe({




  })
}


public onDeleteClient(id: number): void {

  this.clientservice.delete(id).subscribe({
  next:(response: void) => {console.log(response); this.getClients();},
  error: (error:HttpErrorResponse) => error.message,
  complete: () => console.info('complete') 


  })
}
public searchClients(key:string):void{
const results : Client[]=[]; 
for (const client of this.clients){
 if(client.firstname.toLowerCase().indexOf(key.toLowerCase())!==-1){
  results.push(client);
 }

}
this.clients=results;
if(results.length===0 || !key){}
this.getClients();
}










    public onOpenModal(client:Client,mode: string): void{
      const container= document.getElementById('main-container');

      const button=document.createElement('button');
      button.type='button';
      button.style.display='none';
      button.setAttribute('data-toggle','model');
      if(mode ==='add'){
        button.setAttribute('data-target','#addClientModal');

      }

      if(mode ==='adit'){
        this.editClient=client;
        button.setAttribute('data-target','#updateClientModal');

      }

      if(mode ==='delete'){
        this.deleteClient=client
        button.setAttribute('data-target','#deleteClientModal');

      }
      container?.appendChild(button);
      button.click();
    }

  }
