import { Component, OnInit } from '@angular/core';
import { Client} from './client';
import { clientService } from './client.service';
import { HttpErrorResponse } from '@angular/common/http';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  public clients!: Client[];
  public editClient!: Client|null;
  public deleteClient!: Client|null;

  constructor(private clientservice: clientService){}

  ngOnInit() {
    this.getClients();
  }

  public getClients(): void {
    this.clientservice.getClient().subscribe(
      (response: Client[]) => {

        this.clients = response;
        console.log(this.clients);
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    );
  }

  public onAddClient(addForm: NgForm): void {
    document.getElementById('add-employee-form')!.click();
    this.clientservice.addClient(addForm.value).subscribe(
      (response: Client) => {
        console.log(response);
        this.getClients();
        addForm.reset();
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
        addForm.reset();
      }
    );
  }

  public onUpdateClient(id: number): void {
    this.clientservice.update(id).subscribe(
      (response:void) => {
        console.log(response);
        this.getClients();
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    );
  }

  public onDeleteClient(ClientId: number): void {
    this.clientservice.delete(ClientId).subscribe(
      (response: void) => {
        console.log(response);
        this.getClients();
      },
      (error: HttpErrorResponse) => {
        alert(error.message);
      }
    );
  }

  public searchclients(key: string): void {
    console.log(key);
    const results: Client[] = [];
    for (const employee of this.clients) {
      if (employee.firstname.toLowerCase().indexOf(key.toLowerCase()) !== -1
      || employee.email.toLowerCase().indexOf(key.toLowerCase()) !== -1
      || employee.phonenumber.toLowerCase().indexOf(key.toLowerCase()) !== -1
      || employee.casediscription.toLowerCase().indexOf(key.toLowerCase()) !== -1) {
        results.push(employee);
      }
    }
    this.clients = results;
    if (results.length === 0 || !key) {
      this.getClients();
    }
  }

  public onOpenModal(client :Client|null, mode: string): void {
    const container = document.getElementById('main-container');
    const button = document.createElement('button');
    button.type = 'button';
    button.style.display = 'none';
    button.setAttribute('data-toggle', 'modal');
    if (mode === 'add') {
      button.setAttribute('data-target', '#addEmployeeModal');
    }
    if (mode === 'edit') {
      this.editClient = client;
      button.setAttribute('data-target', '#updateEmployeeModal');
    }
    if (mode === 'delete') {
      this.deleteClient= client;
      button.setAttribute('data-target', '#deleteClientModal');
    }
    container!.appendChild(button);
    button.click();
  }



}
